import React ,{ useState } from "react";
import {Table} from 'reactstrap';
import './App.css';
import CircularProgress from '@material-ui/core/CircularProgress';
import {FaDeskpro} from 'react-icons/fa';
var link=["https://reqres.in/api/users?page=1","https://reqres.in/api/users?page=2"];
var i=0;
const App=()=>{
  const [users,setUsers]=useState([]);
  const[cirin,cirfin]=useState(false);
  const load=() =>
  { 
    cirfin(true);
    fetch(link[i])
    .then((response) => response.json())
    .then((json) => {
      console.log(json.data)
      setUsers(json.data); // Prints result from `response.json()` in getRequest
    });
    setTimeout(()=>{
    cirfin(false);
      },100);
    i>=1?i=0:i++;
   };
  return (
    <div className="App">
      <header>
        <nav className='navbar'>
        {cirin  ?'':<button onClick={load}>GET USERS</button>};
        <span><FaDeskpro />ZIGZAG</span>
        </nav>
        {cirfin ?
        <div className='circle'>
        <CircularProgress color='red' size='100px'/>    
        </div>
            :''
        }
       </header>
    <div>
      {users.map((data) => {
            return (
              <div className='card'>
                <img src={data.avatar} alt='profilepic' className="propic"></img>
                <Table>
                <tr>
                  <th>FIRST NAME</th>
                  <td>{data.first_name}</td>
                </tr>
                <tr>
                  <th>LAST NAME</th>
                  <td>{data.last_name}</td>
                </tr>
                <tr>
                  <th>EMAIL ID:</th>
                  <td>{data.email}</td>
                </tr>
                </Table>
              </div>
            );
          })}
      </div>
    </div>
  );
}
export default App;
