import React ,{ useState } from "react";
import './App.css';
import {FaDeskpro} from 'react-icons/fa';
import { CircularProgress } from "@material-ui/core";
var link=["https://reqres.in/api/users?page=1","https://reqres.in/api/users?page=2"];
var i=0;
const App=()=>{
  const [users,setUsers]=useState([]);
  const [preload,loading]=useState(false);
  const load=() =>
  { 
    loading(true);
    fetch(link[i])
    .then((response) => response.json())
    .then((json) => {
      console.log(json.data)
      setUsers(json.data); // Prints result from `response.json()` in getRequest
    });
    i>=1?i=0:i++;
    setTimeout
    (()=>
      {loading(false);},300
    );
  };
  return (
    <div className="App">
      <header>
        <nav class='navbar'>
        <span><FaDeskpro />ZIGZAG</span>
        </nav>
       </header>
       <div class="loader">
         {
           loading?'':<button onClick={load}>GET USERS</button>
         }
       </div>
      <div>
      {users.map((data) => {
            return (
              <div className='card'>
                <img src={data.avatar} alt='profilepic' class="propic"></img>
                <span>
                  First Name:{data.first_name};
                </span>
                <span>
                Last Name:{data.Last_name}
                </span>
                <span>Email:{data.email} </span>
              </div>
            );
          })}
      </div>
    </div>
  );
}
export default App;
