import React ,{ useState } from "react";
import './App.css';

function App(){
  const [users,setUsers]=useState([]);
  const [preload, setload] = useState(false);
  const load=() =>
  { 
    setload(true);
    fetch('https://reqres.in/api/users?page=1')
    .then((response) => response.json())
    .then((json) => {
      console.log(json.data)
      setUsers(json.data); // Prints result from `response.json()` in getRequest
    });
    setTimeout(() => {
      setload(false);
    }, 1000);;
};
  return (
    <div className="App">
      <header>
        <nav class='navbar'>
          { preload? '' : <button onClick={load}>Get Details</button>}
        </nav>
      </header>
      <div>
      {users.map((item) => {
            return (
              <div className='profile'>
                <div className='image'>
                  <img src={item.avatar} alt='User-Image'></img>
                </div>
                <p>
                  {item.first_name} {item.last_name}
                </p>
                <p>{item.email} </p>
              </div>
            );
          })}
      </div>
    </div>
  );
}
export default App;
