import React ,{ useState } from "react";
import './App.css';
import { FaAdn } from 'react-icons/fa';
function App()
{
  const [users, userDetails] = useState([]);

  const [isLoading, setLoading] = useState(false);

  const  loadUsers = () => {
    setLoading(true);
    fetch('https://reqres.in/api/users?page=1')
      .then((response) => response.json())
      .then((json) => {
        console.log(json.data);
        userDetails(json.data);
      });
    setTimeout(() => {
      setLoading(false);
    }, 1000);
  };
  return (
    <div className='UserCard'>
      <header>
        <a href='#' class='logo'>
          <FaAdn /> Users Op
        </a>
        <nav class='navbar'>
          {isLoading ? '' : <button onClick={loadUsers}>Get Details</button>}
        </nav>
      </header>
      <div className='data'>
          {users.map((item) => {
            return (
              <div className='profile'>
                <div className='image'>
                  <img src={item.avatar} alt='User-Image'></img>
                </div>
                <p>
                  {item.first_name} {item.last_name}
                </p>
                <p>{item.email} </p>
              </div>
            );
          })}
        </div>
      </div>
);
}
export default App;
